CREATE TABLE about_us (idnews INT AUTO_INCREMENT, images VARCHAR(120) DEFAULT 'news.jpg', title TEXT, summary TEXT, content TEXT, created_at DATETIME NOT NULL, updated_at DATETIME NOT NULL, category_about_us_idcategory_about_us INT, priority INT DEFAULT '1', PRIMARY KEY(idnews, category_about_us_idcategory_about_us)) ENGINE = INNODB;
CREATE TABLE admin (idadmin INT AUTO_INCREMENT, username VARCHAR(45) NOT NULL, password VARCHAR(45) NOT NULL, fullname VARCHAR(120) DEFAULT 'admin', created_at DATETIME, updated_at DATETIME, PRIMARY KEY(idadmin)) ENGINE = INNODB;
CREATE TABLE category_about_us (idcategory_about_us INT AUTO_INCREMENT, name VARCHAR(45), created_at DATETIME, updated_at DATETIME, PRIMARY KEY(idcategory_about_us)) ENGINE = INNODB;
CREATE TABLE category_gallery (idcategory_about_us INT AUTO_INCREMENT, name VARCHAR(45), created_at DATETIME, updated_at DATETIME, PRIMARY KEY(idcategory_about_us)) ENGINE = INNODB;
CREATE TABLE category_news (idcategory INT AUTO_INCREMENT, name TEXT NOT NULL, created_at DATETIME NOT NULL, updated_at DATETIME, PRIMARY KEY(idcategory)) ENGINE = INNODB;
CREATE TABLE category_service (idcategory INT AUTO_INCREMENT, name TEXT, created_at DATETIME NOT NULL, updated_at DATETIME NOT NULL, PRIMARY KEY(idcategory)) ENGINE = INNODB;
CREATE TABLE doctor (iddoctor INT AUTO_INCREMENT, name VARCHAR(45), degree TEXT, summary TEXT, content TEXT, avatar VARCHAR(120) DEFAULT 'doctor.jpg', created_at DATETIME NOT NULL, updated_at DATETIME NOT NULL, specialist_idspecialist INT, priority INT DEFAULT '1', PRIMARY KEY(iddoctor, specialist_idspecialist)) ENGINE = INNODB;
CREATE TABLE feed_back (idfeed_back INT AUTO_INCREMENT, name_customer VARCHAR(45) NOT NULL, address VARCHAR(45) NOT NULL, avatar VARCHAR(120) DEFAULT 'avatar.jpg', content TEXT, created_at DATETIME NOT NULL, updated_at DATETIME NOT NULL, priority INT DEFAULT '1', PRIMARY KEY(idfeed_back)) ENGINE = INNODB;
CREATE TABLE gallery (idgallery INT AUTO_INCREMENT, name TEXT, url VARCHAR(120) DEFAULT 'gallery.jpg', created_at DATETIME, updated_at DATETIME, category_gallery_idcategory_about_us INT, priority INT DEFAULT '1', PRIMARY KEY(idgallery, category_gallery_idcategory_about_us)) ENGINE = INNODB;
CREATE TABLE news (idnews INT AUTO_INCREMENT, images VARCHAR(120) DEFAULT 'news.jpg', title TEXT, summary TEXT, content TEXT, created_at DATETIME NOT NULL, updated_at DATETIME NOT NULL, category_news_idcategory INT, priority INT DEFAULT '1', PRIMARY KEY(idnews, category_news_idcategory)) ENGINE = INNODB;
CREATE TABLE schedule (idschedule INT AUTO_INCREMENT, fullname VARCHAR(45), age INT, address TEXT, phone VARCHAR(15), date DATE, description TEXT, status INT DEFAULT '0', created_at DATETIME NOT NULL, updated_at DATETIME NOT NULL, PRIMARY KEY(idschedule)) ENGINE = INNODB;
CREATE TABLE service (idnews INT AUTO_INCREMENT, images VARCHAR(120) DEFAULT 'news.jpg', title TEXT, summary TEXT, content TEXT, created_at DATETIME NOT NULL, updated_at DATETIME NOT NULL, category_service_idcategory INT, priority INT DEFAULT '1', PRIMARY KEY(idnews, category_service_idcategory)) ENGINE = INNODB;
CREATE TABLE specialist (idspecialist INT AUTO_INCREMENT, name TEXT, created_at DATETIME NOT NULL, updated_at DATETIME NOT NULL, PRIMARY KEY(idspecialist)) ENGINE = INNODB;
CREATE TABLE service (idnews INT AUTO_INCREMENT, images VARCHAR(120) DEFAULT 'news.jpg', title TEXT, summary TEXT, content TEXT, category_idcategory INT, created_at DATETIME NOT NULL, updated_at DATETIME NOT NULL, PRIMARY KEY(idnews, category_idcategory)) ENGINE = INNODB;
ALTER TABLE about_us ADD CONSTRAINT acci FOREIGN KEY (category_about_us_idcategory_about_us) REFERENCES category_about_us(idcategory_about_us);
